import Board from "../Board";
import {NavButton,FunctionButton} from "../Buttons";

import { putDownPiece, checkWinning } from "../GameLogic"
import { useState } from "react";
import ShowText from "../ShowText";
import chooseRandomMove from "./AIplayers/RandomPlayer"
let boardArrangement1vsAI=[
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "]
  ];

function PlayerVsComputer(props){

    let whoPlaysFirst=props.gameSetting.whoGoFirst;
    let AIalgorithm=props.gameSetting.computer;
    const [turnState,setTurn]=useState({
    isBlackTurn:true
  });
      const [gameState,setGame]=useState({
        isEnded:false,
        winner:null
      })
      const [moveState,setMove]=useState({
        moveMade:[]
      })


    if(whoPlaysFirst!=="Player 1"){
        let moveMade=AImakeMove(AIalgorithm,"Black",boardArrangement1vsAI)
        setMove({moveMade:moveMade})
        setTurn({isBlackTurn:turnState.isBlackTurn?false:true});    
    }

    function AImakeMove(AI,turn,board){
        let computerMove;
        if(AI==="Random"){
            computerMove=chooseRandomMove(board)
        } else if(AI==="Minimax") {
        } else if(AI==="Greedy"){
        }
        putDownPiece(computerMove,turn,board)
        return computerMove
    } 


    function handleMoveClick(clickedIntersectionCoord){
      
      let turn=turnState.isBlackTurn?"B":"W";
      let rowCoord, colCoord;
      let isMoveMade;
      let whoWin;

      [rowCoord,colCoord]=clickedIntersectionCoord;
      isMoveMade = putDownPiece(clickedIntersectionCoord,turn,boardArrangement1vsAI);
      if (isMoveMade){
        whoWin =checkWinning(turn,rowCoord,colCoord,boardArrangement1vsAI);
        if (whoWin!==null){
          setGame({
            isEnded:true,
            winner:whoWin
          })
        }; 
        // console.log(moveState.moveMade)
        setMove({moveMade:clickedIntersectionCoord})
        setTurn({isBlackTurn:turnState.isBlackTurn?false:true});       
      } 

      turn=turnState.isBlackTurn?"B":"W"
      let moveMade=AImakeMove(AIalgorithm,turn,boardArrangement1vsAI)
      whoWin =checkWinning(turn,moveMade,boardArrangement1vsAI);
      if (whoWin!==null){
        setGame({
          isEnded:true,
          winner:whoWin
        })
      }; 
    }

    function getWinningDeclaration(whowin){
        if (whowin==="B"){
            return ("Black piece win !")
        } else if(whowin==="W" ){
            return ("White piece win !")
        } else if (whowin==="D" ){
            return ("Game Draw")
        }
    }

    function resetBoard(board){
        let rowIndex,colIndex;
        for (rowIndex=0;rowIndex<board.length;rowIndex++){
          for (colIndex=0;colIndex<board[rowIndex].length;colIndex++){
            if(board[rowIndex][colIndex]!==" "){
                board[rowIndex][colIndex]=" "
            }
          }
                   
        }
    }

    function resetGame(board){
        resetBoard(board)
        setTurn({isBlackTurn:true})
        setGame({isEnded:false, winner:null})
        setMove({moveMade:[]})
    }

    function returnHome(board){
      props.onClick();
      resetGame(board)
    }


    // console.log(gameState.isEnded)
    // console.log(gameState.winner)
    // console.log(boardArrangement1vs1)
    return(
        <div>
        <div className="rows">
        <NavButton text="Home" onClick={()=>{
                returnHome(boardArrangement1vsAI)
            }}/>

            <h1>Player 1 VS Player 2</h1>
        </div>
        

        <div className="gameScreen">

                <div className="gameboard">        
                    <Board
                        boardArrangement={boardArrangement1vsAI}
                        onClick={gameState.isEnded?
                                    null:
                                    ([rowNum,colNum])=>handleMoveClick([rowNum,colNum])}
                        moveMade={moveState.moveMade}
                        />
                </div>
                <div className="des">
                
                    <FunctionButton text="reset" onClick={()=>resetGame(boardArrangement1vsAI)}/>
                    {
                    gameState.isEnded?
                    <ShowText textColour="textRed"
                        condition={gameState.isEnded}
                        textIfTrue={getWinningDeclaration(gameState.winner)}
                        textIfFalse=""
                     />:
                   <ShowText textColour="textBlack"
                        condition={turnState.isBlackTurn}
                        textIfTrue="Turn: Black"
                        textIfFalse="Turn: White"
                    />
                    }
                    <ShowText textColour="textBlack"
                        condition={true}
                        textIfTrue="Black Piece: Player 1"
                        textIfFalse=""
                    />
                       <ShowText textColour="textBlack"
                        condition={true}
                        textIfTrue="white Piece: Player 2"
                        textIfFalse=""
                    />                  
                </div>

        </div>
        </div>
    )
}

export default PlayerVsComputer;
