import React, { useState } from "react";
import Board from "./Board";
import {putDownPiece,checkWinning} from "./GameLogic";
import HomePage from "./HomePage";
import PlayerVsPlayer from "./PlayerVsPlayer";


function GomokuGame (){

  const [homePageState,setHomeState]=useState({
    showPage:true
  })
  function handleModeClick(mode){
      setHomeState({showPage:homePageState.showPage?false:null})
  }

  function handleHomeClick(){
        setHomeState({showPage:homePageState.showPage?true:null})
  }

return (
  <div>


    <div>
      {homePageState.showPage?<HomePage onClick={()=>handleModeClick()}/>:null}
    </div>
    <div>
      {homePageState.showPage?null:<PlayerVsPlayer />}
    </div>

  </div>
)



}

export default GomokuGame;
