import Board from "./Board";
import NavButton from "./Buttons";
import initialBoard from "./SharedData";
import { putDownPiece, checkWinning } from "./GameLogic"
import { useState } from "react";

let boardArrangement=[
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "],
    [" "," "," "," "," "," "," "," "," "," "," "," "," "," "," "]
  ]
function PlayerVsPlayer(props){
    

    const [turnState,setTurn]=useState({
    isBlackTurn:true
  });
      const [gameState,setGame]=useState({
        isEnded:false,
        winner:null
      })
      const [moveState,setMove]=useState({
        moveMade:[]
      })
    function handleMoveClick(clickedIntersectionCoord){
      
      let turn=turnState.isBlackTurn?"B":"W";
      let rowCoord, colCoord;
      let isMoveMade;
      let whoWin;
      console.log("isblackturn"+turnState.isBlackTurn);
      console.log("whosTurn:"+turn);
      [rowCoord,colCoord]=clickedIntersectionCoord;
      isMoveMade = putDownPiece(clickedIntersectionCoord,turn,boardArrangement);
      if (isMoveMade){
        whoWin =checkWinning(turn,rowCoord,colCoord,boardArrangement);
        if (whoWin!==null){
          setGame({
            isEnded:true,
            winner:whoWin
          })
        }; 
        setMove({moveMade:clickedIntersectionCoord})
        setTurn({isBlackTurn:turnState.isBlackTurn?false:true});
      } 

    }
    // console.log(boardArrangement)
    return(
        <div>
        <div>
            <NavButton text="Home" onClick={null}/>
        </div>
        

        <div className="game row">        
            <Board
                boardArrangement={boardArrangement}
                onClick={([rowNum,colNum])=>handleMoveClick([rowNum,colNum])}
                moveMade={moveState.moveMade}
                />
        </div>

        </div>
    )
}

export default PlayerVsPlayer;
